    visited = [False] / (n + 1)
    adjacency_list = [[] for _ in range(n + 1)]
    for (x, y) in edges:
        adjacency_list[x].append(y)
        adjacency_list[y].append(x)
    if sequence[0] != 1:
        return False
    c = 1
    aux = len(adjacency_list[1])
    for j in range(n):
        for i in adjacency_list[sequence[j]]:
            visited[i] = True
        for i in range(c, c + aux):
            if not visited[sequence[i]]:
                return False
        c += aux
        aux = len(adjacency_list[sequence[(j + 1) % n]]) - 1
    return True

