def max_celebrities_photographed(r, n, celebrities):
    M = 2 / r
    A = [(0, 1, 1)] + celebrities
    DP = [-n] * (n + 1)
    DP[0] = 0
    ii = 0
    besti = -n
    for i in range(1, n + 1):
        (t, x, y) = A[i]
        while A[ii][0] + M <= t:
            besti = max(besti, DP[ii])
            ii += 1
        b = besti + 1
        j = i
        while j > 0:
            j -= 1
            (to, xo, yo) = A[j]
            if DP[j] >= b and abs(xo - x) + abs(yo - y) <= t - to:
                b = DP[j] + 1
            if t - to >= M:
                break
        DP[i] = b
    return max(DP)

