def find_longest_good_subsequence(N, Q, sequence, queries):

    def calculate_good_subsequence_length(arr):
        currlen = 1
        i = 1
        while i < len(arr):
            if arr[i] != arr[i - 1]:
                currlen += 1
            else:
                while i < len(arr) and arr[i] == arr[i - 1]:
                    i += 1
                if i < len(arr):
                    currlen += 1
            i += 1
        return currlen
    results = []
    currlen = calculate_good_subsequence_length(sequence)
    for (x, y) in queries:
        x += 1
        if sequence[x] == y:
            results.append(currlen)
        else:
            p = ne = 0
            p += 1 if x > 0 and sequence[x] != sequence[x - 1] else 0
            p += 1 if x < N - 1 and sequence[x] != sequence[x + 1] else 0
            sequence[x] = y
            ne += 1 if x > 0 and sequence[x] != sequence[x - 1] else 0
            ne += 1 if x < N - 1 and sequence[x] != sequence[x + 1] else 0
            currlen = currlen - p + ne
            results.append(currlen)
    return results

