def find_kth_flower_position(N, K, WD):

    def is_ok(X):
        need = 1
        for (w, d) in WD:
            if w <= X:
                need += (X - w) // d + 1
        return need >= K

    def binary_search_meguru():
        ng = -1
        ok = max((w + K * d for (w, d) in WD)) + 1
        while abs(ok - ng) > 1:
            mid = ng + (ok - ng) // 2
            if is_ok(mid):
                ok = mid
            else:
                ng = mid
        return ok
    return binary_search_meguru()

