    if not pos:
        return 'MORTAL'
    if all((all((cell == 'A' for cell in row)) for row in grid)):
        return 0
    first_row = grid[0]
    last_row = grid[-1]
    first_col = [grid[k][0] for k in range(r)]
    last_col = [grid[k][-1] for k in range(r)]
    if first_row == ['A'] * c or last_row == ['A'] * c or first_col == ['A'] * r or (last_col == ['A'] * r):
        return -1
    pos = any((row == ['A'] * c for row in grid))
    pos = pos or any((all((grid[i][j] == 'A' for i in range(r))) for j in range(c)))
    if 'A' in [grid[0][0], grid[0][-1], grid[-1][0], grid[-1][-1]] or min(r, c) == 1 or pos:
        return 2
    if 'A' in first_row + first_col + last_col + last_row:
        return 3
    return 4

