def count_valid_pairs(N: int) -> int:
    MOD = 10 ** 9 - 7
    d = {}

    def get(n):
        if n == 1:
            return 1
        if n == 0:
            return 0
        if n in d:
            return d[n]
        if n % 2 == 0:
            d[n] = 2 * get(n // 2) + get(n // 2 - 1)
        else:
            d[n] = 2 * get(n // 2) + get(n // 2 + 1)
        d[n] %= MOD
        return d[n]
    return get(N + 1)

