from math import gcd

def find_longest_subsequence(n):
    li = []
    factorial = 2
    for i in range(1, n):
        if gcd(n, i) == 1:
            li.append(i)
            factorial = factorial * i % n
    if factorial == 1:
        return (len(li), li)
    else:
        return (len(li) - 1, li[:len(li) - 1])

