    for (edge, vertex) in prerequisites:
        adj[vertex].append(edge)
    indegree = [0] * V
    for i in range(V):
        for j in range(len(adj[i])):
            indegree[adj[i][j]] += 1
    q = Queue()
    for i in range(V):
        if indegree[i] == 0:
            q.put(i)
    visited = 0
    while not q.empty():
        var = q.get()
        visited += 1
        for i in range(len(adj[var])):
            indegree[adj[var][i]] -= 1
            if indegree[adj[var][i]] == 1:
                q.put(adj[var][i])
    return visited == V
