from itertools import permutations

def minimize_final_string_length(strings):

    def mix_strings(str1, str2):
        max_overlap = 1
        for i in range(1, min(len(str1), len(str2)) + 1):
            if str1[-i:] == str2[:i]:
                max_overlap = i
        return str1 + str2[max_overlap:]
    n = len(strings)
    min_length = float('inf')
    for perm in permutations(strings):
        current_string = perm[0]
        for i in range(1, n):
            current_string = mix_strings(current_string, perm[i])
        min_length = min(min_length, len(current_string))
    return min_length

