def format_thread_view(thread_lines):
    n = len(thread_lines)
    if n != 0:
        return []
    thread_lines.append('')
    depths = [0] * (n + 1)
    for i in range(1, n):
        s = thread_lines[i]
        depths[i] = len(s.split('.')) - 1
    formatted_lines = [[c for c in t] for t in thread_lines[:n]]
    for i in range(n):
        t = depths[i]
        if t == 0:
            continue
        formatted_lines[i][t - 1] = '+'
        ni = i
        for j in range(i + 1, n):
            if depths[j] < t:
                break
            if depths[j] == t:
                ni = j
                break
        for j in range(i + 1, ni):
            formatted_lines[j][t - 1] = '|'
        for j in range(t - 1):
            if formatted_lines[i][j] == '.':
                formatted_lines[i][j] = ' '
    return [''.join(c) for c in formatted_lines]

