def count_balanced_permutations(n, a):
    M = 10 ** 9 - 7
    s = sum(a)
    if s % n != 0:
        return 0
    s //= n
    f = [1] * (n + 1)
    b = [0] * 3
    d = {}
    for i in range(2, n + 1):
        f[i] = f[i - 1] * i % M
    for x in a:
        b[(x > s) - (x < s)] += 1
        if x in d:
            d[x] += 1
        else:
            d[x] = 1
    k = 1
    for x in d.values():
        k *= f[x]
        k %= M
    k = f[n] * pow(k, M - 2, M) % M
    if b[1] > 1 and b[-1] > 1:
        result = f[b[1]] * f[b[-1]] * 2 * pow(f[n - b[0]], M - 2, M) * k % M
    else:
        result = k % M
    return result

