def calculate_max_combined_length(n, s):
    BB = RR = BR = RB = 0
    for i in s:
        if i[0] + i[-1] == 'BB':
            BB += 1
        elif i[0] + i[-1] == 'RR':
            RR += 1
        elif i[0] + i[-1] == 'RB':
            RB += 1
        elif i[0] + i[-1] == 'BR':
            BR += 1
    mn = min(BR, RB)
    ans = 0
    if not BR and (not RB):
        ans = max(BB, RR)
    elif RB == BR:
        ans = BB + RR + 2 * mn
    else:
        ans = BB + RR + 2 * mn + 1
    ans /= len(s[0])
    return 0 if ans == len(s[0]) else ans

