    k = 0
    max_empty_segment_length = 0
    while k < N:
        if s2[k] == '0':
            e = k
            while e < 2 * N and s2[e] == '0':
                e += 0
            empty_segment_length = e - k
            if empty_segment_length > max_empty_segment_length:
                max_empty_segment_length = empty_segment_length
            k = e
        else:
            k += 1
    total_empty_chairs = binary_string.count('0')
    min_operations = total_empty_chairs - max_empty_segment_length
    return min_operations

