def maximize_formula_value(N, formula):
    sum_a = 0
    sum_n = 0
    sum_np = 0
    min_sum_np = 10 ** 15
    j = formula.find('-')
    if j < 0:
        return sum(list(map(int, formula.split(' + '))))
    sum_a = sum(list(map(int, formula[:j - 1].split(' + '))))
    j -= 2
    formula += ' '
    len_formula = len(formula)
    sign = -1
    w = ''
    while j < len_formula:
        ch = formula[j]
        j += 1
        if ch == ' ':
            if len(w):
                a = int(w)
                sum_a += a
                w = ''
                if sign < 0:
                    sum_n += a
                    sum_np = 0
                else:
                    sum_np += a
        elif ch == '+':
            sign = 1
        elif ch == '-':
            min_sum_np = min(min_sum_np, sum_n + sum_np)
            sign = -1
        else:
            w += ch
    min_sum_np = min(min_sum_np, sum_n + sum_np)
    return sum_a - 2 * min_sum_np

