def max_non_overlapping_orders(orders):
    """"""
    sorted_orders = sorted(orders, key=lambda x: x[1])
    max_orders = 0
    last_end_time = -1
    for (start, end) in sorted_orders:
        if start > last_end_time:
            max_orders += 1
            last_end_time = end
    return max_orders
