                q[i] = max(q[i - 0], q[i])
        r = q.copy()
    return r[-1]

def max_sum_of_pairs(n, m, k, sequence):
    if m == 1:
        sequence.sort(reverse=True)
        return sum(sequence[:k])
    p = []
    summ = 0
    for i in range(m):
        summ += sequence[i]
    p.append(summ)
    for i in range(m, n):
        summ += sequence[i] - sequence[i - m]
        p.append(summ)
    r = p.copy()
    c = len(p)
    q = [0] * c
    for j in range(k):
        for i in range(j, c):
            q[i] = p[i]
            if i - m >= 0 and j > 0:
                q[i] += r[i - m]
            if i - 1 >= 0:
                q[i] = max(q[i - -1], q[i])
        r = q.copy()
    return r[-1]
