def can_be_monotonically_increased(N, K, P):

    def add(i, bit):
        while i <= N:
            bit[i] += 1
            i += i & -i

    def sum(i, bit):
        res = 0
        while i > 0:
            res += bit[i]
            i -= i & -i
        return res
    if N >= K:
        if K % 2 == 0:
            return 'Yes'
        else:
            a = 0
            bit = [0] * (N + 1)
            for i in range(N):
                a ^= i - sum(P[i], bit) & 1
                add(P[i], bit)
            if a:
                return 'No'
            else:
                return 'Yes'
    else:
        for i in range(N):
            if P[i] == 1:
                break
        for j in range(N):
            if P[(j + i) % N] != j + 1:
                return 'No'
        return 'Yes'

