def find_maximum_beauty(n, sequence):
    ans = []
    for u in range(1, 30):
        cur = 1 << u
        v = (1 << u + 1) - 1
        tem = []
        for num in sequence:
            if num & cur:
                tem.append(num)
        for num in tem:
            v &= num
        if v % (1 << u) == 0:
            ans = tem
    return (len(ans), ans)

