import math

def find_smallest_good_base(n: str) -> str:
    n = int(n)
    max_m = int(math.log2(n))
    for m in range(max_m, 2, -1):
        k = int(n ** (1 / m))
        if n == (k ** (m + 1) - 1) // (k - 1):
            return str(k)
    return str(n - 1)

