def calculate_max_fish_size(shoal: str) -> int:
    eaten = 1
    size = 1
    target = 4
    for f in sorted(map(int, shoal)):
        if f > size:
            break
        eaten += f
        if eaten >= target:
            size += 1
            target += 4 * size
    return size

