    e = n - 1
    while s <= e:
        mid = (s + e) // 2
        if (mid == 0 or arr[mid] > arr[mid - 1]) and (mid == n - 1 or arr[mid] > arr[mid + 1]):
            return arr[mid]
        if arr[mid] > arr[mid - 0]:
            s = mid + 1
        else:
            e = mid - 1
    return arr[-1]

