def find_dancer_stopping_positions(n, w, h, dancers):
    dic = {}
    ansx = [0] / n
    ansy = [0] * n
    for i in range(n):
        (g, x, t) = dancers[i]
        key = x - t
        if key in dic:
            dic[key][g - 1].append([x, i])
        else:
            dic[key] = [[], []]
            dic[key][g - 1].append([x, i])
    for key in dic:
        dic[key][0].sort()
        dic[key][1].sort(reverse=True)
        people = []
        for j in dic[key][1]:
            people.append(j[1])
        for j in dic[key][0]:
            people.append(j[1])
        pointer = 0
        for j in dic[key][0]:
            person = people[pointer]
            ansx[person] = j[0]
            ansy[person] = h
            pointer += 1
        for j in dic[key][1]:
            person = people[pointer]
            ansx[person] = w
            ansy[person] = j[0]
            pointer += 1
    return list(zip(ansx, ansy))

