def min_replacements_to_equalize_sequence(sequence):
    from collections import Counter
    frequency = Counter(sequence)
    counts = list(frequency.values())
    counts.sort()
    if len(counts) != 3:
        return counts[0] + counts[1]
    elif len(counts) == 2:
        return counts[0]
    else:
        return 0

