from operator import sub, mul
from functools import reduce

def generate_name_color(name: str) -> str:
    if len(name) <= 2:
        return None
    ord_values = list(map(ord, name))
    r = sum(ord_values) % 256
    g = reduce(mul, ord_values) % 256
    b = abs(reduce(sub, ord_values)) % 256
    return '{:02X}{:02X}{:02X}'.format(r, g, b)

