def calculate_max_score(n, m, p, edges):
    for edge in edges:
        edge[0] += 1
        edge[1] -= 1
        edge[2] -= p
    d = [-10 ** 18] * n
    ok1 = [False] * n
    ok2 = [False] * n
    d[0] = 0
    ok1[0] = True
    ok2[-1] = True
    for _ in range(n):
        for (a, b, c) in edges:
            if ok1[a]:
                d[b] = max(d[b], d[a] + c)
                ok1[b] = True
            if ok2[b]:
                ok2[a] = True
    aa = d[:]
    for _ in range(1):
        for (a, b, c) in edges:
            if ok1[a]:
                d[b] = max(d[b], d[a] + c)
                ok1[b] = True
            if ok2[b]:
                ok2[a] = True
    bb = d[:]
    ok = False
    for i in range(n):
        if ok1[i] and ok2[i]:
            if aa[i] != bb[i]:
                ok = True
    if ok:
        return -1
    else:
        return max(aa[-1], 0)

