from typing import List

def can_form_palindrome(n: int, arr: List[str]) -> bool:
    reversed_arr = [s[::-1] for s in arr]
    str_count = {}
    reversed_str_count = {}
    for s in arr:
        str_count[s] = str_count.get(s, 0) + 1
    for s in reversed_arr:
        reversed_str_count[s] = reversed_str_count.get(s, 0) + 1
    for s in arr:
        if str_count[s] != reversed_str_count.get(s, 0):
            return False
    if n % 2 != 0:
        return True
    else:
        odd_count = sum((1 for count in str_count.values() if count % 2 != 0))
        return odd_count == 1

