def calculate_remaining_nodes(n, parents):

    def dfs(node):
        nonlocal adj, leaf
        val = 0
        flag = 0
        for i in adj[node]:
            x = dfs(i)
            val += x
            if x == 0:
                flag = 1
        leaf -= val - val % 3
        if val % 3 == 0 and flag == 0:
            return 1
        else:
            return 0
    adj = [[] for _ in range(n + 2)]
    leaf = 0
    for i in range(2, n + 1):
        adj[parents[i - 2]].append(i)
    dfs(1)
    return n - leaf

