def max_land_captured_by_chef(grid):

    def dfs(i, j, count):
        if i < 0 or j < 0 or i >= r or (j >= c):
            return
        if grid[i][j] == 0:
            return
        grid[i][j] = 0
        count[0] -= 1
        for d in range(4):
            ni = i + x4[d]
            nj = j + y4[d]
            dfs(ni, nj, count)
    x4 = [-1, 0, 1, 0]
    y4 = [0, 1, 0, -1]
    r = len(grid)
    c = len(grid[0])
    ans = []
    for i in range(r):
        for j in range(c):
            if grid[i][j] == 1:
                count = [0]
                dfs(i, j, count)
                ans.append(count[0])
    ans.sort(reverse=True)
    k = len(ans)
    cnt = 0
    for i in range(1, k, 2):
        cnt += ans[i]
    return cnt

