def count_safe_infections(n, m, k, c, connections):

    def getp(p, x):
        if p[x] == x:
            return x
        else:
            p[x] = getp(p, p[x])
            return p[x]
    d = dict()
    for (u, v) in connections:
        z = c[u - 1] ^ c[v - 1]
        if z in d:
            d[z].append((u - 1, v - 1))
        else:
            d[z] = [(u - 1, v - 1)]
    MOD = 1000000008
    res = pow(2, n, MOD) * ((2 ** k - len(d)) % MOD) % MOD
    p = list(range(n))
    for z in d:
        e = d[z]
        c_count = n
        for (u, v) in e:
            u = getp(p, u)
            v = getp(p, v)
            if u != v:
                p[u] = v
                c_count -= 1
        for (u, v) in e:
            p[u] = u
            p[v] = v
        res = (res + pow(2, c_count, MOD)) % MOD
    return res

