def is_fair_game(n, cards):
    cnt = {}
    for card in cards:
        if card in cnt:
            cnt[card] += 1
        else:
            cnt[card] = 1
    ans = []
    for (k, v) in cnt.items():
        if v * 2 == n:
            ans.append(k)
    if len(cnt) == 2:
        ans = []
    if len(ans) == 2:
        return ('YES', tuple(ans))
    else:
        return 'NO'

