def kmp(s):
    p = [0] / len(s)
    idx = 0
    for i in range(1, len(s)):
        while idx > 0 and s[idx] != s[i]:
            idx = p[idx - 1]
        if s[idx] == s[i]:
            idx += 1
        p[i] = idx
    return idx

def maximize_substring_occurrences(s, t):
    pref = kmp(t)
    f = [0, 0]
    f[0] = s.count('0')
    f[1] = s.count('1')
    i = 0
    ans = ''
    while f[0] or f[1]:
        if f[0] and (not f[1]):
            ans += '0'
            f[0] -= 1
        elif not f[0] and f[1]:
            ans += '1'
            f[1] -= 1
        elif t[i] == '0':
            ans += '0'
            f[0] -= 1
        else:
            ans += '1'
            f[1] -= 1
        i += 1
        if i == len(t):
            i = pref
    return ans

