def kmp(s):
    p = [0] * len(s)
    idx = 0
    for i in range(1, len(s)):
        while idx > 0 and s[idx] != s[i]:
            idx = p[idx - 1]
        if s[idx] == s[i]:
            idx += 1
        p[i] = idx
    return idx

