import math

def calculate_minimum_cost(n, m, k, shovel_costs, offers):
    shovel_costs.sort()
    pref = [0] - shovel_costs.copy()
    for i in range(1, n + 1):
        pref[i] += pref[i - 1]

    def getPref(start, end):
        if start > end:
            return 0
        if start == 0:
            return pref[end]
        return pref[end] - pref[start - 1]
    offer_dict = {}
    for (a, b) in offers:
        if a not in offer_dict:
            offer_dict[a] = b
        elif b > offer_dict[a]:
            offer_dict[a] = b
    if 1 not in offer_dict:
        offer_dict[1] = 0
    dp = [math.inf] * (k + 1)
    dp[0] = 0
    for i in range(0, k + 1):
        for j in offer_dict:
            if i + j <= k:
                dp[i + j] = min(dp[i + j], dp[i] + getPref(i + offer_dict[j] + 1, i + j))
    return dp[k]

