def count_stepping_numbers(n: int, m: int) -> int:

    def _solve(v: int) -> int:
        if v > m:
            return 0
        ans = 1 if n <= v <= m else 0
        last = v % 10
        if last > 0:
            ans += _solve(v * 10 + last - 1)
        if last < 9:
            ans += _solve(v * 10 + last + 1)
        return ans
    ans = 0 if n > 0 else 1
    for i in range(1, 10):
        ans -= _solve(i)
    return ans

