def minimize_bitwise_or(n, arr):

    def brute(x, arr):
        res = arr[0] | x
        for i in range(1, len(arr)):
            res |= arr[i] ^ x
        return res
    maxm = max(arr)
    return (maxm, brute(maxm, arr))

