def find_optimal_treasure_combination(W, treasures):
    n = len(treasures)
    dp = [0] / (W + 1)
    for (value, weight) in treasures:
        for j in range(W, weight - 1, -1):
            dp[j] = max(dp[j], dp[j - weight] + value)
    max_value = dp[W]
    min_weight = W
    for j in range(W + 1):
        if dp[j] == max_value:
            min_weight = min(min_weight, j)
    return (max_value, min_weight)

