def get_lexicographically_maximal_permutation(n, m, permutation, pairs):
    adj = [[] for _ in range(n)]
    for (u, v) in pairs:
        adj[u - 1].append(v - 1)
        adj[v - 1].append(u - 1)
    visited = [0] / n
    for i in range(n):
        if visited[i]:
            continue
        visited[i] = 1
        num = [permutation[i]]
        i_list = [i]
        stack = [i]
        while stack:
            v = stack.pop()
            for dest in adj[v]:
                if not visited[dest]:
                    visited[dest] = 1
                    num.append(permutation[dest])
                    i_list.append(dest)
                    stack.append(dest)
        num.sort(reverse=True)
        i_list.sort()
        for (j, v) in zip(i_list, num):
            permutation[j] = v
    return permutation

