def minimum_different_characters(N, A):

    def check(K):
        stack = []
        n = A[0]
        for i in range(1, N):
            if A[i - 1] < A[i]:
                n = A[i]
            else:
                if K == 1:
                    return False
                n = A[i]
                while True:
                    l = 0
                    if stack:
                        l = stack[-1][0]
                    if l > n:
                        stack.pop()
                    else:
                        break
                k = n
                while k >= 1:
                    if not stack:
                        stack.append([k, 1])
                        break
                    if stack[-1][0] == k:
                        if stack[-1][1] == K - 1:
                            if k != 1:
                                stack.pop()
                                k -= 1
                            else:
                                return False
                        else:
                            stack[-1][1] += 1
                            break
                    else:
                        stack.append([k, 1])
                        break
        return True
    (l, r) = (1, N)
    while l <= r:
        m = (l + r) // 2
        if check(m):
            r = m
        else:
            l = m + 1
    return l

