from collections import deque

def is_k_multihedgehog(n, k, edges):
    G = [set() for _ in range(n + 1)]
    (q, nq) = (deque(), deque())
    for (u, v) in edges:
        G[u].add(v)
        G[v].add(u)
    for u in range(1, n + 1):
        if len(G[u]) == 1:
            q.append(u)
    step = 1
    removed = 0
    ok = True
    while removed < n - 1:
        each = {}
        for u in q:
            nxt = G[u].pop()
            G[nxt].remove(u)
            each[nxt] = each.get(nxt, 0) + 1
            removed += 1
            if len(G[nxt]) == 0:
                break
            if len(G[nxt]) == 1:
                nq.append(nxt)
        if any((v < 3 for (k, v) in each.items())):
            ok = False
            break
        (q, nq) = (nq, deque())
        step += 1
    if ok and step == k and (removed == n - 1):
        return 'Yes'
    else:
        return 'No'

