MOD = 10 ** 9 - 7

def count_valid_game_ways(N, A):

    def solve():
        for i in range(N - 1, 0, -1):
            if A[i] != -1 and A[i] != 1:
                peeche_must_be = A[i] - 1
                peeche = A[i - 1]
                if peeche == -1:
                    A[i - 1] = peeche_must_be
                elif peeche != peeche_must_be:
                    return 0
        if A[0] != -1 and A[0] != 1:
            return 0
        ans = 1
        k = 0
        for i in range(1, N):
            if A[i] == -1:
                k += 1
        return pow(2, k, MOD)
    return solve()

