def calculate_minimum_time(n: int, m: int) -> int:
    """"""

    def binpow(a, n):
        res = 1
        while n > 0:
            if n & 1:
                res = res * a % m
            a = a * a % m
            n >>= 1
        return res
    ans = binpow(3, n) - 1
    if ans % m == 0 and ans != 0:
        return m - 1
    else:
        return ans % m
