def maximize_total_income(chemforces_elements, topchemist_elements):
    chemforces_dict = {indx: income for (indx, income) in chemforces_elements}
    total_income = sum(chemforces_dict.values())
    for (indx, income) in topchemist_elements:
        if indx not in chemforces_dict:
            total_income -= chemforces_dict[indx]
            total_income += max(income, chemforces_dict[indx])
        else:
            total_income += income
    return total_income

