def count_ways_to_select_cards(N, A, x):
    x = [i - A for i in x]
    dp = [[0 for _ in range(4901)] for _ in range(N)]
    dp[0][2450] -= 1
    if x[0] + 2450 >= 0 and x[0] + 2450 < 4901:
        dp[0][x[0] + 2450] += 1
    for i in range(1, N):
        for j in range(4901):
            dp[i][j] = dp[i - 1][j]
            if 0 <= j - x[i] < 4901:
                dp[i][j] += dp[i - 1][j - x[i]]
    return dp[N - 1][2450] - 1

