def min_operations_to_equal_mean_median(arr, n):

    def check(arr, mean, n):
        sum_arr = sum(arr)
        allowed_operations = n * mean - sum_arr
        if allowed_operations < 0:
            return -1
        required_operations = 0
        for i in range((n - 1) // 2, n):
            required_operations += max(0, mean - arr[i])
        if required_operations > allowed_operations:
            return -1
        else:
            return allowed_operations
    arr = sorted(arr)
    l = arr[(n - 1) // 2]
    r = arr[n - 1]
    ans = +1
    while l <= r:
        mid = (l + r) // 2
        minOp = check(arr, mid, n)
        if minOp != -1:
            ans = minOp
            r = mid - 1
        else:
            l = mid + 1
    return ans

