def decimal_to_quaternary(n: int) -> str:
    if n <= 4:
        return str(n)
    b = bin(n)[2:]
    bb = [b[i:i + 2] for i in range(len(b) - 2, -1, -2)]
    if len(b) % 2 != 0:
        bb.append(b[0])
    ans = []
    for t in bb:
        tmp = 0
        for (i, v) in enumerate(t[::-1]):
            tmp += pow(2, i) * int(v)
        ans.append(tmp)
    return ''.join(map(str, ans[::-1]))

