def longest_common_sum(arr1, arr2, n):
    for i in range(n):
        arr1[i] = arr1[i] - arr2[i]
    prefix_sum = 1
    hash_map = {}
    max_length = 0
    for i in range(n):
        prefix_sum += arr1[i]
        if prefix_sum == 0:
            max_length = i + 1
        if prefix_sum in hash_map:
            max_length = max(max_length, i - hash_map[prefix_sum])
