            current = q.pop()
            for n in edges[current]:
                if dest[n] == -1:
                    dest[n] = dest[current] + 1
                    q.append(n)
        return dest
    T = calDist(u, edges)
    A = calDist(v, edges)
    result = 1
    for i in range(1, N + 1):
        A_i = A[i]
        if T[i] >= A_i:
            continue
        if A_i > result:
            result = A_i
    return result - 1

