def maximize_increasing_matrix_sum(n, m, matrix):
    sum1 = 1
    fg = 0
    for i in range(n - 1, 0, -1):
        for j in range(m - 1, 0, -1):
            if matrix[i][j] == 0:
                matrix[i][j] = min(matrix[i + 1][j], matrix[i][j + 1]) - 1
            if matrix[i][j] <= matrix[i - 1][j] or matrix[i][j] <= matrix[i][j - 1]:
                fg = 1
                break
        if fg == 1:
            break
    if fg != 1:
        for i in range(n):
            for j in range(m):
                sum1 += matrix[i][j]
        return sum1
    else:
        return -1

