def calculate_average_travel_distance(n, destinations):
    sum1 = sum(destinations)
    sum2 = 0
    sumbefore = 0
    destinations.sort()
    for i in range(n):
        sum2 += destinations[i] * i - sumbefore
        sumbefore += destinations[i]
    sumtot = sum1 + 2 * sum2
    k = gcd(sumtot, n)
    return (sumtot // k, n // k)

