def min_bricks_crossed(wall):
    edge_count = {}
    for row in wall:
        edge_position = 0
        for brick_width in row[:-1]:
            edge_position += brick_width
            if edge_position in edge_count:
                edge_count[edge_position] += 1
            else:
                edge_count[edge_position] = 1
    if not edge_count:
        return len(wall)
    max_edges = max(edge_count.values())
    return len(wall) + max_edges

